import React, { useContext, useState } from "react"; 
import TransitionLayout from "../../components/layout/TransitionLayout";
import BasicPageTitle from '../../components/layout/pagetitle/BasicPageTitle';  
import { SocketContext } from '../../lib/providers/socketContext';   

import { useChatState } from '../../lib/providers/chatProvider'; 
import { TRoom, TChatMessage } from '../../types/chat';

const ChatPage = ():JSX.Element => {   
    const socket = useContext(SocketContext); 
    const chat = useChatState();
    const [message, setMessage] = useState('');
    const handleChangeMessage = (e:any) => {  
      setMessage(e.target.value);
    }

    const handleMessage = () => { 


    const payload:TChatMessage = {
      id: 3,
      nickname: "학구", 
      profile: "profile2",
      datetime: 'datetime2',
      message, 
      msgType: 'image2',
      source: 'source2'
      } 

      socket.emit('msgToServer', payload);   
      // 채팅 메세지 발송   
    }

    // socket.on('msgToClient', (payload:TChatMessage) => { 
    //   // 채팅 메세지 수신  
    //   console.log(payload); 
    //   chatDispatch({type:"PUSH_CHAT_LIST", payload});    
    // });


    return (  
    <>
      <BasicPageTitle title="커뮤니티" description="커뮤니티 페이지 작업예정" />  
          <TransitionLayout title="채팅" requiredLogin={true}>  
           <input type='text' value={message} onChange={handleChangeMessage}/> 
          <button onClick={handleMessage}>SEND</button>
          {JSON.stringify(chat)}  

      </TransitionLayout>
    </> 
  ); 
};


export default ChatPage;
