import React, { useState,useEffect } from 'react';
import styled from "styled-components";
import { TRoom } from "../../types/chat"; 
import { FaUser } from 'react-icons/fa';
import { HiDotsHorizontal } from 'react-icons/hi';
import { dateFormet } from "../../globalFunction";
import dayjs from 'dayjs';

const ExPersonalChatWrapper = styled.div`  
  width:100%;
  height:100%;
  display:flex;
  flex-flow: row wrap;
`;

const PersonalList = styled.div`
  background: #fff;
  margin: 40px auto;
  width: 100%;
  @media (min-width: 992px) { 
    width: 50%; 
  }
`; 

const PChatItemWrapper = styled.div`
  width: 100%;
  padding: 7px 7px 0 7px;
  display:flex;
  flex-flow: row wrap;
  position:relative;
  transition: all .3s;
  cursor:pointer;

  :hover{
    background: #ededed;
    .time_box{
      display:none;
    }
    .option{
      display:block;
      color: grey;
    }
  }
  .option{
    position:absolute;
    top:10px;
    right:10px;
    font-size:20px;
    display:none;
  }

  .option_detail{
    width:140px;
    background:#fff;
    position:absolute;
    right:5px;
    top:5px;
    font-size:14px;
    box-shadow: 0 0 10px rgba(0,0,0,0.3);
    z-index:99;
    ul{
      width:100%;
      li{
          padding:10px;
          cursor: pointer;
          :hover{
            background:	#0080ff;
            color: #fff;
          }
      }
    }
  }

  .avatar{
    width:70px;
    height:70px;
    display:flex;
    justify-content: center;
    // margin-top:10px;
    img{width:50%; height:50%;object-fit:cover; border-radius:50%;}
    .anonimous{
      width:50%;
      height:50%;
      font-size:20px;
      display:flex;
      justify-content:center;
      align-items:center;
      background:#359cdb;
      color: #fff;
      border-radius:50%;
    }
  }
  .info{
    width: calc(100% - 70px);
    padding:3px 10px;


    .room_name{
      font-size:16px;
      font-weight:700;
      margin-bottom:4px;
    }
    .last_msg{
      font-size: 14px;
      overflow: hidden;
      text-overflow: ellipsis;
      display: -webkit-box;
      -webkit-line-clamp: 2; /* 라인수 */
      -webkit-box-orient: vertical;
      word-wrap:break-word; 
      line-height: 1.2em;
      height: 30px; 
    }
    .time_box{
      font-size:12px; 
      position:absolute;
      right:10px;
      top:10px;
      color:grey;
    }
  }
`;  


const UserList = styled.div`
  width:100%;
  @media (min-width: 992px) { 
    width: 50%; 
  }
`;


interface Props { 

}


interface PCItemProps { 
  readonly item: TRoom; 
}
// dateFormet
const personals: TRoom[] = [ 
  {
    id: 0,
    roomKey: 'roombot1',
    roomName: '챗봇01',
    lastMsg: '반갑습니다 ^^',
    lastDatetime: '2021-02-10 14:00:00',
    seen: false,
    profile:''
  },
  {
    id: 0,
    roomKey: 'roombot2',
    roomName: '챗봇02',
    lastMsg: '반갑습니다 ^^',
    lastDatetime: '2021-02-10 14:00:00',
    seen: false,
    profile:'https://images.mypetlife.co.kr/content/uploads/2019/09/04222847/dog-panting-1024x683.jpg'
  },
  {
    id: 0,
    roomKey: 'roombot3',
    roomName: '챗봇03', 
    lastMsg: '반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ 반갑습니다 ^^ ',
    lastDatetime: '2021-02-10 14:00:00',
    seen: false,
    profile:'https://images.mypetlife.co.kr/content/uploads/2019/09/04222847/dog-panting-1024x683.jpg'
  }

];

const ExPersonalChat = ({  }:Props):JSX.Element=> {

  return (
    <ExPersonalChatWrapper> 

      {personals.length === 0 && <p>No Data</p>}   
      <PersonalList>
          <p>나의 채팅방</p> 
          {personals.map((item: TRoom) => (
            <PersonalChatItem item={item}/>
          ))}  
      </PersonalList>

      <UserList>
        <p>접속 유저 목록</p>  

      </UserList>
    </ExPersonalChatWrapper> 
  )

}



const PersonalChatItem = ({ item }: PCItemProps): JSX.Element => {   

  const [openOpt, setOpenOpt] = useState(false);
  const itemRef = React.useRef(null); 

  const handleOpenOpt = () => {
    setOpenOpt(true); 
    return;
  }
  const handleCloseOpt = () => {
    setOpenOpt(false);
    return;
  }

  const useOutsideAlerter = (ref: any) => {
    useEffect(() => {
      function handleClickOutside(event: any) {
        if (ref.current && !ref.current.contains(event.target)) { 
          setOpenOpt(false); 
        }
      } 

      // Bind the event listener
      document.addEventListener("mousedown", handleClickOutside);
      return () => {
        // Unbind the event listener on clean up
        document.removeEventListener("mousedown", handleClickOutside);

      };
    }, [ref]);
  };
  useOutsideAlerter(itemRef);


  return (
    <PChatItemWrapper>
      <div className="avatar">
        { item.profile ? (<img src={item.profile}  alt='avatar'/>) : (<div className="anonimous"><FaUser /></div>)}
      </div>
      <div className="info"> 
        <div className="room_name">
          {item.roomName}
        </div>
        <div className="last_msg">
          {item.lastMsg} 
        </div>
        {!openOpt && (
        <div className="time_box"> 
          { dateFormet(dayjs(item.lastDatetime).format('HH:mm'))} 
        </div> 
        )}
        <div className="option" onClick={ handleOpenOpt }>
          <HiDotsHorizontal />  
        </div>
        {openOpt && (<div ref={itemRef} className="option_detail">
                  <ul>
                    <li>채팅방 나가기</li>
                    <li onClick={handleCloseOpt}>취소</li>
                  </ul>
                </div>)}
      </div> 
    </PChatItemWrapper>
  );
}


export default ExPersonalChat;
