import React,{useState} from "react";
import Link from 'next/link';
import styled from "styled-components";
import { RiMenu3Line } from "react-icons/ri";

const BasicNavigation = styled.ul`
  position:absolute;
  right:15px;
  top:0px;
  .menu_icon{
    padding:0 10px 10px 10px;
    font-size:25px;

  }
  .nav_list{
    position:absolute;
    top:0;
    right:0;
    width:320px;
    background:#fff;
    // display:none; 
  }
`;


const BasicNav = () => {
  const [open, setOpen] = useState(true); 

  const handleOpenNavi = () => {
    setOpen(true);
  }

  return (
    <BasicNavigation>
      <div className='menu_icon' onClick={handleOpenNavi}>   
        <RiMenu3Line/>
      </div>
      {open && <ul className="nav_list">
        <li><Link href=''><a>Nav1</a></Link></li>
        <li><Link href=''><a>Nav2</a></Link></li>
        <li><Link href=''><a>Nav3</a></Link></li>
        <li><Link href=''><a>Nav4</a></Link></li>
        <li><Link href=''><a>Nav5</a></Link></li> 
      </ul>}

    </BasicNavigation>
  )
}

export default BasicNav;
