import styled from "styled-components";
import { Swiper, SwiperSlide } from "swiper/react";
import { GrNext } from 'react-icons/gr';

const BasicSlideBannerWrapper = styled.div`
  width:100%;
  height:100%;
  display:flex;
  flex-flow: row wrap;

`;


const SwiperItem = styled(Swiper)`
  img{width:100%;}

  .swiper-button-prev:after, .swiper-button-next:after{
    display:none;
  }

  .swiper-button-prev,.swiper-button-next{
    color: #fff;
    content:'';    
    background-image: url(${props.themet.next}) !important; 
  }

`;


interface BannerItem{
  id: number;
  img_url: string;
  target_url: string;
}

interface Props { 
  datas: BannerItem[]; 
}

const invertTheme = {
  next: <GrNext />,
  prev: <GrNext />
}

const BasicSlideBanner = ({ datas }:Props):JSX.Element=> {

  const handleRedirect = (href: string) => {
    window.location.href = href;
    return;
  }

  return (
    <BasicSlideBannerWrapper>
       <SwiperItem
        spaceBetween={0}  
        slidesPerView={1}
        speed={400}
        navigation
        autoplay  
        pagination={{ clickable: true }}
        onSlideChange={() => console.log("slide change")}
        onSwiper={(swiper) => console.log(swiper)}
        loop={true} 
      >
        {datas.map((item:BannerItem) => (
          <SwiperSlide key={item.img_url + item.target_url} themet={invertTheme}>
            <img src={item.img_url} alt="banner" onClick={()=>{ handleRedirect(item.target_url) }}/> 
          </SwiperSlide>          
        ))} 

      </SwiperItem>
    </BasicSlideBannerWrapper>
  )

}

export default BasicSlideBanner;
