import { BACKEND_URL } from 'config';
import styled from 'styled-components';
import useSWR from 'swr';
import axios from 'util/axios';
import TransitionLayout from 'components/layout/TransitionLayout';
// import queryString from 'query-string';
import dayjs from 'dayjs';
import { useState } from 'react';
const BoardViewPageWrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-flow: row wrap;

  .board-header {
    width: 100%;
    border-bottom: 1px solid lightgrey;
    padding: 15px 0;
    .created {
      color: grey;
    }
  }
  .board-body {
    width: 100%;
    padding: 30px 15px;
    .author {
      width: 100%;
      margin-top: 40px;
      background: #ededed;
      padding: 15px 20px;
      border-radius: 10px;
    }
  }
  .comments {
    width: 100%;
    .comments-header {
      width: 100%;
    }
    .comment-form {
      width: 100%;
      padding: 15px;
      background: #f8f8ff;
      border-radius: 5px;
      display: flex;
      flex-flow: row wrap;
      textarea {
        width: calc(100% - 80px);
      }
      .comment-submit {
        width: 80px;
        background: #00aedb;
        color: #fff;
      }
    }
  }
`;

interface Props {}

const BoardViewPage = ({}: Props): JSX.Element => {
  // const parsed = queryString.parse(location.search);
  const fetcher = async (url: string) => {
    const resp = await axios.get(url);
    return resp.data;
  };

  const { data, mutate, error } = useSWR(`${BACKEND_URL}/board/17`, fetcher);
  if (!data) return <div>Loading</div>;
  if (error) return <div>ERROR</div>;
  return (
    <TransitionLayout title="채팅" requiredLogin={true}>
      <BoardViewPageWrapper>
        <div className="board-header">
          <h2>{data.title}</h2>
          <div className="created">
            {dayjs(data.createdAt).format('YYYY.MM.DD HH:mm')}
          </div>
        </div>

        <div className="board-body">
          <div dangerouslySetInnerHTML={{ __html: data.content }} />
          <div className="author">작성자 : {data.author}</div>
        </div>

        <Comments />
      </BoardViewPageWrapper>
    </TransitionLayout>
  );
};

const Comments = () => {
  const [content, setContent] = useState('');

  const handleChangeContent = (e: any) => {
    setContent(e.target.value);
    return;
  };

  return (
    <div className="comments">
      <div className="comments-header">댓글 0개</div>
      <div className="comment-form">
        <textarea onChange={handleChangeContent}>{content}</textarea>
        <button className="comment-submit">댓글 등록</button>
      </div>
    </div>
  );
};

export default BoardViewPage;
