import styled from "styled-components";
import { IoMdSend } from 'react-icons/io';
import React, { useState } from 'react';


const ChatFormWrapper = styled.form`
  width:100%;
  height:100%;
  display:flex;
  flex-flow: row wrap;
  position:relative;

  .chatting_input{
    width:100%;
    height:40px;
    border: 1px solid lightgrey;
    box-shadow:none;
    padding-left:15px;
  }

  .chatting_submit{
    background:none;
    position:absolute;
    right:10px;
    top:5px;
    font-size:20px;
    color: #0080FF;
  }

`;

interface Props { 

}

const ChatForm = ({  }:Props):JSX.Element=> {

  const [message, setMessage] = useState('');

  const handleSubmit = (e:any) => {
    e.preventDefault();
    setMessage(''); 
  }

  const handleChangeMessage = (e: any) => {
    setMessage(e.target.value);
  }

  return (
    <ChatFormWrapper onSubmit={handleSubmit}>
      <input className="chatting_input" type="text" onChange={handleChangeMessage}/> 
      <button className="chatting_submit">
        <IoMdSend/>
      </button>
    </ChatFormWrapper>
  )

}

export default ChatForm;
