import { useState } from 'react';
import styled from 'styled-components';
import TransitionLayout from 'components/layout/TransitionLayout';
import EditorField from 'components/form/EditorField';
import draftToHtml from 'draftjs-to-html';
import BasicTextField from 'components/form/material/BasicTextField';
import { EditorState, convertToRaw } from 'draft-js';
import { Formik, Form } from 'formik';

const BoardWriteWrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-flow: row wrap;
`;

const SubmitBtn = styled.button`
  box-shadow:none;
  font-size:16px;
  padding:10px 20px;
  border-radius 5px;
  background: #1a1a1a;
  color: #fff;
`;

interface Props {}

const BoardWritePage = ({}: Props): JSX.Element => {
  const [editorState, setEditorState] = useState(EditorState.createEmpty());

  const handleSubmit = () => {
    //   HTML화 된 값
    const editorToHtml = draftToHtml(
      convertToRaw(editorState.getCurrentContent())
    );

    console.log(editorToHtml.toString());
    return;
  };

  return (
    <TransitionLayout>
      <BoardWriteWrapper>
        <>
          <Formik
            initialValues={{
              title: '',
              content: '',
              tags: [
                {
                  tag_name: '',
                },
              ],
            }}
            validate={(values) => {
              const errors: Record<string, string> = {};

              if (values.title === '') {
                errors.text = '제목을 입력해주세요.';
              }

              return errors;
            }}
            onSubmit={(data, { setSubmitting }) => {
              setSubmitting(true);

              console.log(data);

              setSubmitting(false);
            }}
          >
            <Form>
              <BasicTextField name="text" />
              <EditorField val={editorState} setVal={setEditorState} />

              {/* 수정모드일때만  */}
              {/* <EditorField
          val={editorState}
          setVal={setEditorState}
          editMode={true}
          currentVal="<p><b>HTML</b> 입니다!</p>"
        /> */}
              <SubmitBtn onClick={handleSubmit}>작성완료</SubmitBtn>
            </Form>
          </Formik>
        </>
      </BoardWriteWrapper>
    </TransitionLayout>
  );
};

export default BoardWritePage;
