import React, { useState } from "react";
import styled from "styled-components";
import Link from "next/link";
import axios from "axios";
import { TextField } from "@material-ui/core";
import { BACKEND_URL } from '../../config';

const RegisterForm = () => { 
  const [email, setEmail] = React.useState("");
  const [nickname, setNickname] = React.useState("");
  const [password, setPassword] = React.useState("");
  const [passwordConfirm, setPasswordConfirm] = React.useState("");
  const [agree, setAgree] = React.useState(false);

  // ValidationForm
  const [valiEmail, setValiEmail] = useState("");
  const [valiPwd, setValiPwd] = useState("");
  const [valiPwdConfirm, setValiPwdConfirm] = useState("");

  const handleOnSubmit = async (e:any) => {
    // e.preventDefault();
    alert("TQ");
    return;

    if(!validationEmail(email, true)){ 
      return false;
    }

    if (!validationPasswd(password, true)) {
      return false;
    }

    if (!validationPasswdConfirm(passwordConfirm, true)) {
      return false;
    }

    if (!email || !password || !passwordConfirm) {
      alert("정보를 모두 입력해주세요");
      return;
    }

    if (password !== passwordConfirm) {
      alert("비밀번호와 비밀번호 확인이 일치하지 않습니다.");
      setPassword("");
      setPasswordConfirm("");
      return;
    }

    try {
      const token:any = await axios 
        .post(BACKEND_URL + "/auth/register", { 
          user_id: email,
          password,
        })
        .catch((err) => {
          console.log(err);
        });

      console.log(token);
      if (token.status === 200) {
        alert("회원가입 성공!"); 
        return;
      } else {
        alert("회원가입 실패");
        return;
      }
    } catch (err) {
      alert(err);
      setEmail("");
      setPassword("");
      setPasswordConfirm("");
      return;
    }
  };


  // 이메일 정규표현식 체크
  const validationEmail = (email, submit) => {
    // var email = e.target.value;
    setEmail(email);
    var exptext = /^[A-Za-z0-9_\.\-]+@[A-Za-z0-9\-]+\.[A-Za-z0-9\-]+/;
    if (exptext.test(email) === false) {
      if (email === "") {
        //이메일 형식이 알파벳+숫자@알파벳+숫자.알파벳+숫자 형식이 아닐경우
        if (submit) {
          alert("이메일은 필수 입력 항목입니다.");
        } else {
          setValiEmail("이메일은 필수 입력 항목입니다.");
        }
      } else {
        if (submit) {
          alert("이메일 형식이 올바르지 않습니다.");
        } else {
          setValiEmail("이메일 형식이 올바르지 않습니다.");
        }
      }
      return false;
    }
    setValiEmail("");
    return true;
  };

  const validationPasswd = (pw, submit) => {
    setPassword(pw);
    var num = pw.search(/[0-9]/g);
    var eng = pw.search(/[a-z]/gi);

    if (pw.length < 8 || pw.length > 20) {
      if (submit) {
        alert("8자리 ~ 20자리 이내로 입력해주세요.");
      } else {
        setValiPwd("8자리 ~ 20자리 이내로 입력해주세요.");
      }
      return false;
    } else if (pw.search(/\s/) !== -1) {
      if (submit) {
        alert("비밀번호는 공백 없이 입력해주세요.");
      } else {
        setValiPwd("비밀번호는 공백 없이 입력해주세요.");
      }
      return false;
    } else if (num < 0 || eng < 0) {
      if (submit) {
        alert("영문,숫자를 혼합하여 입력해주세요.");
      } else {
        setValiPwd("영문,숫자를 혼합하여 입력해주세요.");
      }
      return false;
    } else {
      setValiPwd("");
      return true;
    }
  };

  const validationPasswdConfirm = (pwConfirm, submit) => {
    setPasswordConfirm(pwConfirm);
    if (password !== pwConfirm) {
      if (submit) {
        alert("비밀번호와 비밀번호 확인이 일치하지 않습니다.");
      } else {
        setValiPwdConfirm("비밀번호와 비밀번호 확인이 일치하지 않습니다.");
      }
      return false;
    } else {
      setValiPwdConfirm("");
      return true;
    }
  };

  return (
    <ViewWrapper>
      <LoginFormWrapper>
        <Title>Sign up</Title>
        <LoginTextField
          label="이메일"
          type="text"
          val={email}
          setVal={setEmail}
        />
        { valiEmail}
        <LoginTextField
          label="닉네임"
          type="text"
          val={nickname}
          setVal={setNickname}
        />

        <LoginTextField
          label="비밀번호"
          type="password"
          val={password}
          setVal={setPassword}
        />
        { valiPwd}
        <LoginTextField
          label="비밀번호 확인"
          type="password"
          val={passwordConfirm}
          setVal={setPasswordConfirm}
        />
        { valiPwdConfirm}
        <CheckboxWrapper>
          <label htmlFor="longsession">
            개인정보 처리방침 동의 (필수)
            <input
              id="longsession"
              type="checkbox"
              onChange={() => {
                setAgree(!agree);
              }}
            />
          </label>
          &nbsp;&nbsp;
          <a target="_blank" href="/agree">
            내용보기
          </a>
          <br />
          <br />
          <label htmlFor="longsession">
            서비스 이용약관 동의 (필수)
            <input
              id="longsession"
              type="checkbox"
              onChange={() => {
                setAgree(!agree);
              }}
            />
          </label>
          &nbsp;&nbsp;
          <a target="_blank" href="/agree">
            내용보기
          </a>
        </CheckboxWrapper>
        <SubmitButton onClick={handleOnSubmit} text="회원가입" /> 

        <NoAccount>
          이미 계정이 있으신가요? <Link href="/auth/login">로그인</Link>
        </NoAccount>
      </LoginFormWrapper>
    </ViewWrapper>
  );
};
const Title = styled.div`
  width: 100%;
  font-size: 30px;
  font-weight: 700;
`;
const ViewWrapper = styled.form`
  display: flex;
  flex-flow: row wrap;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100vh;
  padding: 10px;
  background: #ededed;
`;
const LoginFormWrapper = styled.div`
  position: relative;
  width: 500px;
  height: auto;
  background: white;
  border-radius: 5px;
  padding: 60px 25px;
  @media (min-width: 768px) {
    padding: 80px 40px;
  }
  @media (min-width: 1024px) {
    padding: 90px 60px;
  }
`;

const NoAccount = styled.div`
  width: 100%;
  position: absolute;
  text-align: center;
  bottom: -35px;
  left: 0;
`;

const CheckboxWrapper = styled.div`
  font-size: 14px;
  input {
    margin-left: 6px;
    position: relative;
    top: 2px;
  }
`;

const LoginTextField = (props) => {
  const { label, type, val, setVal } = props;
  return (
    <FieldWrapper>
      <TextField
        label={label}
        fullWidth
        value={val}
        type={type}
        onChange={(e) => {
          setVal(e.target.value);
        }}
      />
    </FieldWrapper>
  );
};

const FieldWrapper = styled.div`
  width: 100%;
  padding: 5px;
  margin-bottom: 15px;
`;


const SubmitButton = (props) => {
  const { text } = props;
  return <SubmitBtn>{text}</SubmitBtn>;
};

const SubmitBtn = styled.button`
  box-shadow: none;
  border: none;
  width: 100%;
  line-height: 45px;
  background: #0abab5;
  color: #fff;
  font-size: 16px;
  margin-top: 30px;

  @media (min-width: 768px) {
    font-size: 17px;
  }
  @media (min-width: 1024px) {
    line-height: 50px;
    font-size: 18px;
  }
`;

export default RegisterForm;
