    const payload:TChatMessage = {
      id: 3,
      nickname: "시발",
      profile: "profile2",
      datetime: 'datetime2',
      message, 
      msgType: 'image2',
      source: 'source2'
      } 

      chatDispatch({type:"PUSH_CHAT_LIST", payload});    
