import React, { Dispatch, SetStateAction } from 'react';
import styled from 'styled-components';
import dynamic from 'next/dynamic';
import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';
import {
  EditorState,
  convertToRaw,
  convertFromHTML,
  ContentState,
} from 'draft-js';
import draftToHtml from 'draftjs-to-html';

const Editor: any = dynamic(
  () => import('react-draft-wysiwyg').then((mod) => mod.Editor),
  {
    ssr: false,
  }
);

const EditorFieldWrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-flow: row wrap;
`;

const PaddingWrapper = styled.div`
  width: 100%;
  padding: 15px;
`;

interface Props {
  val: EditorState;
  setVal: Dispatch<SetStateAction<EditorState>>;
  editMode?: boolean;
  currentVal?: string;
}

const EditorField = ({
  val,
  setVal,
  editMode,
  currentVal,
}: Props): JSX.Element => {
  React.useEffect(() => {
    if (!editMode) return;

    const blocksFromHTML = convertFromHTML(currentVal);

    const state = ContentState.createFromBlockArray(
      blocksFromHTML.contentBlocks,
      blocksFromHTML.entityMap
    );

    setVal(EditorState.createWithContent(state));
  }, []);

  const onEditorStateChange = (editorState: any) => {
    // editorState에 값 설정
    setVal(editorState);
  };

  const handleSubmit = () => {
    //   HTML화 된 값
    const editorToHtml = draftToHtml(convertToRaw(val.getCurrentContent()));

    console.log(editorToHtml.toString());
    return;
  };

  return (
    <EditorFieldWrapper>
      <PaddingWrapper>
        <Editor
          // 에디터와 툴바 모두에 적용되는 클래스
          wrapperClassName="wrapper-class"
          // 에디터 주변에 적용된 클래스
          editorClassName="editor"
          // 툴바 주위에 적용된 클래스
          toolbarClassName="toolbar-class"
          // 툴바 설정
          toolbar={{
            // inDropdown: 해당 항목과 관련된 항목을 드롭다운으로 나타낼것인지
            list: { inDropdown: true },
            textAlign: { inDropdown: true },
            link: { inDropdown: true },
            history: { inDropdown: false },
          }}
          placeholder="내용을 작성해주세요."
          // 한국어 설정
          localization={{
            locale: 'ko',
          }}
          // 초기값 설정
          editorState={val}
          // 에디터의 값이 변경될 때마다 onEditorStateChange 호출
          onEditorStateChange={onEditorStateChange}
        />
      </PaddingWrapper>

      <button onClick={handleSubmit}>작성완료</button>
    </EditorFieldWrapper>
  );
};

export default EditorField;
