import styled from 'styled-components';
import TransitionLayout from 'components/layout/TransitionLayout';
import BoardWriteForm from 'components/form/BoardWriteForm';
import { EditorState } from 'draft-js';

const BoardWriteWrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-flow: row wrap;
`;

interface Props {}

const BoardWritePage = ({}: Props): JSX.Element => {
  // const [datas, setDatas] = React.useState([]);
  // const [editorState, setEditorState] = React.useState(EditorState.createEmpty());
  // const [active, setActive] = React.useState(false);
  // const [targetId, setTargetId] = React.useState(0);

  const handleSubmit = () => {
    //   HTML화 된 값
    const editorToHtml = draftToHtml(
      convertToRaw(editorState.getCurrentContent())
    );

    console.log('TARGET', {
      id: targetId,
      status: status,
      memo: editorToHtml.toString(),
    });

    Axios.put(BACKEND_URL + '/calculate/fit-pictorial', {
      id: targetId,
      status: status,
      memo: editorToHtml.toString(),
    }).then(() => {
      // Editor 초기화
      // window.location.reload();
      setEditorState(EditorState.createEmpty());
      setActive(false);
    });

    return;
  };

  return (
    <TransitionLayout>
      <BoardWriteWrapper>
        <h1>글쓰기</h1>
        <BoardWriteForm />
      </BoardWriteWrapper>
    </TransitionLayout>
  );
};

export default BoardWritePage;
