import styled from 'styled-components';

const SubmitBtn = styled.button`
  box-shadow:none;
  font-size:16px;
  padding:10px 30px;
  border-radius 5px;
  background: #1a1a1a;
  color: #fff;
  width:100%;
  .disabled{
    display:none;
  }
`;

const LoadingSubmitBtn = styled.button`
  box-shadow:none;
  font-size:16px;
  padding:10px 30px;
  border-radius 5px;
  background: #1a1a1a;
  color: #fff;
  width:100%;
  opacity:0.5;
`;
const BasicSubmitBtn = ({ loading }: any): JSX.Element => {
  return (
    <>
      {loading ? (
        <LoadingSubmitBtn className="disabled" type="submit">
          작성완료
        </LoadingSubmitBtn>
      ) : (
        <SubmitBtn className="disabled" type="submit">
          작성완료
        </SubmitBtn>
      )}
    </>
  );
};

export default BasicSubmitBtn;
