import wrapper from "../store/configureStore";
import withReduxSage from "next-redux-saga";
import 'bootstrap/dist/css/bootstrap.css'
import '../scss/global.scss'; 
import Cookies from "js-cookie";
import jwtDecode from "jwt-decode";
import { useDispatch, useSelector } from "react-redux";
import { loadUserRequestAction } from "../reducers/user";

const App = ({ Component }) => {
  const accessToken = Cookies.get("accessToken");
  const dispatch = useDispatch();
  const { me } = useSelector((state:any) => state.user);

  if (accessToken) { 
    dispatch(loadUserRequestAction({ user_id:jwtDecode<any>(accessToken).user_id }));
    console.log("GLOBAL!", jwtDecode<any>(accessToken).user_id); 
    console.log(me);    
  }


  return (
    <>
      <Component /> 
    </>
  );
};

export default wrapper.withRedux(withReduxSage(App));
