// import Link from "next/link";
import React, { useEffect } from 'react';
import Head from 'next/head';
import Cookies from 'js-cookie';
import { useRouter } from 'next/router';
import { useAuthDispatch } from '../../lib/providers/authProvider';
import jwtDecode from 'jwt-decode';
import { BACKEND_URL } from '../../config';
// import Header from './header/AdminHeader';
// import Footer from './footer/AdminFooter';
import { Layout } from 'antd';

const { Header, Footer, Sider, Content } = Layout;
import 'antd/dist/antd.css';

const AdminLayout = ({ children, title, requiredLogin }: any) => {
  // const me = useAuthState();
  // const dispatch = useAuthDispatch();
  const Router = useRouter();
  const accessToken = Cookies.get('accessToken');

  useEffect(() => {
    if (requiredLogin) {
      console.log('로그인 필수 페이지');
      if (!accessToken) {
        // 로그인되어있지 않다면 로그인창으로
        Router.push('/intro');
      }
    }

    if (!accessToken) {
      // 로그인되어있지 않다면 로그인창으로
      // Router.push("/login");
    } else {
      // 로그인 되어있다면

      console.log(BACKEND_URL + '/auth/' + jwtDecode<any>(accessToken).user_id);
      // axios.get(
      //   BACKEND_URL + "/auth/" + jwtDecode<any>(accessToken).user_id
      // ).then((res:any) => {
      //   console.log(res.data);
      //   const userInfo = res.data;
      //   dispatch({ type: "UPDATE_USER", userInfo });
      // })
    }
  }, []);

  return (
    <>
      {/* <Header /> */}
      {/* <Footer /> */}
      <Layout>
        <Head>
          <title>{title}</title>
          <meta charSet="utf-8" />
          <meta
            name="viewport"
            content="user-scalable=no, initial-scale=1.0, maximum-scale=1.0, width=device-width, target-densityDpi=medium-dpi, viewport-fit=cover"
          />
        </Head>
        <Sider width={200} className="site-layout-background">
          <Menu
            mode="inline"
            defaultSelectedKeys={['1']}
            defaultOpenKeys={['sub1']}
            style={{ height: '100%', borderRight: 0 }}
          >
            <SubMenu key="sub1" icon={<UserOutlined />} title="subnav 1">
              <Menu.Item key="1">option1</Menu.Item>
              <Menu.Item key="2">option2</Menu.Item>
              <Menu.Item key="3">option3</Menu.Item>
              <Menu.Item key="4">option4</Menu.Item>
            </SubMenu>
            <SubMenu key="sub2" icon={<LaptopOutlined />} title="subnav 2">
              <Menu.Item key="5">option5</Menu.Item>
              <Menu.Item key="6">option6</Menu.Item>
              <Menu.Item key="7">option7</Menu.Item>
              <Menu.Item key="8">option8</Menu.Item>
            </SubMenu>
            <SubMenu
              key="sub3"
              icon={<NotificationOutlined />}
              title="subnav 3"
            >
              <Menu.Item key="9">option9</Menu.Item>
              <Menu.Item key="10">option10</Menu.Item>
              <Menu.Item key="11">option11</Menu.Item>
              <Menu.Item key="12">option12</Menu.Item>
            </SubMenu>
          </Menu>
        </Sider>
        <Layout>
          <Header></Header>
          <Content>
            <div className="page-content">{children}</div>
          </Content>
          <Footer>Corp. HAKGU</Footer>
        </Layout>
      </Layout>
    </>
  );
};

export default AdminLayout;
