import styled from 'styled-components';

const SubmitBtn = styled.button`
  box-shadow:none;
  font-size:16px;
  padding:10px 30px;
  border-radius 5px;
  background: #1a1a1a;
  color: #fff;
  .disabled{
    display:none;
  }
`;

interface Props {
  readonly loading: any;
}

const BasicSubmitBtn = ({ children, loading }: Props): JSX.Element => {
  return (
    <>
      <SubmitBtn className="disabled" type="submit">
        작성완료{loading && '트루'}
      </SubmitBtn>
    </>
  );
};

export default BasicSubmitBtn;
