import React, { useContext, useState } from "react"; 
import TransitionLayout from "../../components/layout/TransitionLayout";
import BasicPageTitle from '../../components/layout/pagetitle/BasicPageTitle';  
import { SocketContext } from '../../lib/providers/socketProvider';
import { useChatState, useChatDispatch } from '../../lib/providers/chatProvider';
import { TRoom, TChatMessage } from '../../types/chat';
import useInput from '../../hooks/useInput'; 

const ChatPage = ():JSX.Element => {   
    const socket = useContext(SocketContext);
    const chat = useChatState();
    const chatDispatch = useChatDispatch();

  const [message, handleChageMessage] = useInput('');

  const handleMessage = () => { 

    // const sampleMessages = [{
    //   id: 1,
    //   nickname: "학구",
    //   profile: "profile",
    //   datetime: 'datetime',
    //   message: 'chat',
    //   msgType: 'image',
    //   source: 'source'
    // },
    // {
    //   id: 2,
    //   nickname: "학구",
    //   profile: "profile",
    //   datetime: 'datetime',
    //   message: 'chat',
    //   msgType: 'image',
    //   source: 'source'
    //   }];

    const payload:TChatMessage = {
      id: 3,
      nickname: "시발",
      profile: "profile2",
      datetime: 'datetime2',
      message, 
      msgType: 'image2',
      source: 'source2'
      }

      socket.emit('msgToServer', payload);  
      // 채팅 메세지 발송   
      chatDispatch({type:"PUSH_CHAT_LIST", payload});    
      // chatDispatch({type:"SET_CHAT_LIST", payload: sampleMessages, inRoomKey:'test'});  
    }

    socket.on('msgToClient', (payload:TChatMessage) => { 
      // 채팅 메세지 수신  
      console.log(payload); 
      chatDispatch({type:"PUSH_CHAT_LIST", payload});    
    })


    return (  
    <>
      <BasicPageTitle title="커뮤니티" description="커뮤니티 페이지 작업예정" />  
          <TransitionLayout title="채팅" requiredLogin={true}>  
          COMMUNITY 
           <input type='text' value={message} onChange={handleChageMessage}/> 
          <button onClick={handleMessage}>SEND</button>
          {JSON.stringify(chat)}  

      </TransitionLayout>
    </>
  ); 
};


export default ChatPage;
