import { useState } from 'react';
import styled from 'styled-components';
import TransitionLayout from 'components/layout/TransitionLayout';
import EditorField from 'components/form/EditorField';
import draftToHtml from 'draftjs-to-html';
import BasicTextField from 'components/form/material/BasicTextField';
import { EditorState, convertToRaw } from 'draft-js';
import { Formik, Form } from 'formik';
import axios from 'util/axios';
import { BACKEND_URL } from 'config';
import BasicSubmitBtn from 'components/tools/BasicSubmitBtn';
import { useAuthState } from 'lib/providers/authProvider';
import { useRouter } from 'next/router';

const BoardWriteWrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-flow: row wrap;
`;

interface Props {}

const BoardWritePage = ({}: Props): JSX.Element => {
  const me = useAuthState();

  const [editorState, setEditorState] = useState(EditorState.createEmpty());

  const [loading, setLoading] = useState(false);
  const Router = useRouter();

  return (
    <TransitionLayout>
      <BoardWriteWrapper>
        <>
          <Formik
            initialValues={{
              title: '',
              content: '',
              tags: [
                {
                  tag_name: '',
                },
              ],
            }}
            validate={(values) => {
              const errors: Record<string, string> = {};

              if (values.title === '') {
                errors.title = '제목을 입력해주세요.';
              }

              // if (values.content === '') {
              //   errors.content = '내용을 입력해주세요.';
              // }

              return errors;
            }}
            onSubmit={(data, { setSubmitting }) => {
              setSubmitting(true);
              setLoading(true);
              const editorToHtml = draftToHtml(
                convertToRaw(editorState.getCurrentContent())
              );
              axios
                .post(`${BACKEND_URL}/board`, {
                  title: data.title,
                  content: editorToHtml.toString(),
                  author: me.userInfo.user_id,
                  thumbnail: '',
                  tags: 'tag',
                  topic: 'default',
                })
                .then((res: any) => {
                  Router.push(`/board/${res.data.id}`);
                });

              setSubmitting(false);
            }}
          >
            <Form style={{ width: '100%' }}>
              <BasicTextField name="title" />
              {/* <EditorField val={editorState} setVal={setEditorState} /> */}

              {/* 수정모드일때만  */}
              {/* <EditorField
          val={editorState}
          setVal={setEditorState}
          editMode={true}
          currentVal="<p><b>HTML</b> 입니다!</p>"
        /> */}

              <BasicSubmitBtn text="작성완료" loading={loading} />
            </Form>
          </Formik>
        </>
      </BoardWriteWrapper>
    </TransitionLayout>
  );
};

export default BoardWritePage;
