import React from 'react';
import 'react-quill/dist/quill.snow.css';
import * as Emoji from 'quill-emoji';
import ReactQuill, { Quill } from 'react-quill';

var SizeStyle = Quill.import('attributors/style/size');
SizeStyle.whitelist = [
  '8pt',
  '9pt',
  '10pt',
  '11pt',
  '12pt',
  '14pt',
  '24pt',
  '36pt',
];

Quill.register('modules/emoji', Emoji);
Quill.register(SizeStyle, true);

// function insertHeart() {
//   console.log('THIS-QUILL', this.quill);
//   const cursorPosition = this.quill.getSelection().index;
//   this.quill.insertText(cursorPosition, 'â™¥');
//   this.quill.setSelection(cursorPosition + 1);
// }

const CustomToolbar = () => (
  <div id="toolbarr">
    <select className="ql-font">
      <option value="arial" selected>
        Arial
      </option>
      <option value="comic-sans">Comic Sans</option>
      <option value="courier-new">Courier New</option>
      <option value="georgia">Georgia</option>
      <option value="helvetica">Helvetica</option>
      <option value="lucida">Lucida</option>
    </select>
    <select className="ql-size">
      <option value="8pt" style={{ fontSize: '8pt' }}>
        8pt
      </option>
      <option value="9pt" style={{ fontSize: '9pt' }}>
        9pt
      </option>
      <option value="10pt" style={{ fontSize: '10pt' }}>
        10pt
      </option>
      <option value="11pt" style={{ fontSize: '11pt' }}>
        11pt
      </option>
      <option value="12pt" style={{ fontSize: '12pt' }}>
        12pt
      </option>
      <option value="14pt" style={{ fontSize: '14pt' }}>
        14pt
      </option>
      <option value="24pt" style={{ fontSize: '24pt' }}>
        24pt
      </option>
      <option value="36pt" style={{ fontSize: '36pt' }}>
        36pt
      </option>
    </select>

    <select className="ql-align" />
    <select className="ql-color" />
    <select className="ql-background" />
    <button className="ql-image" />
    <button className="ql-video" />

    <button className="ql-clean" />
    <button className="ql-emoji" />
  </div>
);

const Editor2 = ({ val, setVal, handleWriteSubmit }: any) => {
  // const [content, setContent] = React.useState('');
  const [openEmoji, setOpenEmoji] = React.useState(false);

  const emojiList = ['ðŸ˜€', 'ðŸ˜€'];

  // MDI emojicon instead of default icon
  const emojiIcon =
    '<svg class="i" viewBox="0 0 24 24"><use href="#emoticon-happy"></use></svg>';

  function toggleEmojiBox() {
    setOpenEmoji(true);
  }

  const handleChange = (html: any) => {
    setVal(html);
  };

  const modules = {
    'toolbar': {
      container: '#toolbarr',
      // handlers: {
      //   insertHeart: insertHeart,
      // },
    },
    'emoji-toolbar': true,
    'emoji-textarea': true,
    'emoji-shortname': true,
  };

  return (
    <div className="text-editor">
      <CustomToolbar />
      <ReactQuill value={val} onChange={handleChange} modules={modules} />
    </div>
  );
};

export default Editor2;
