import React, {useState, useEffect} from "react";
// import AppLayout from "../../components/layout/AppLayout";
import TransitionLayout from "../../components/layout/TransitionLayout"; 
import BasicPageTitle from '../../components/layout/pagetitle/BasicPageTitle';  
import { io } from 'socket.io-client';
import { Formik, Form } from "formik";
import BasicTextField from "../../components/form/material/BasicTextField"; 
import { Button } from "@material-ui/core";

interface Message {
  id: string; 
  name: string; 
  text: string;
}

interface Payload{
  name: string;
  text: string;
}


const ChatPage = (): JSX.Element => {   
  const [name, setName] = useState('');
  const [text, setText] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const socket = io('http://localhost:8080');

  useEffect(() => {

    function receivedMessage(message: Payload) {
      const newMessage: Message = {
        id: "test",
        name: message.name,
        text: message.text
      }

      setMessages([...messages, newMessage]);
    }

    socket.on('msgToClient', (message: Payload) => {
      receivedMessage(message);
    });
  }, []);  


  function sendMessage() {
    const message: Payload = {
      name,
      text
    }
    socket.emit('msgToServer', message);
  }

  return ( 
    <>
      <BasicPageTitle title="채팅" description="채팅 페이지 작업예정" />  
      <TransitionLayout title="채팅" requiredLogin={true}>  
        CHAT 

        <Formik initialValues={{ name:"hakgu", text: "" }}
        validate={values => {
          const errors: Record<string, string> = {} 
          // text에  !나 ? 가 들어가면 에러를 뿜는 예제 
          if (values.text.includes("!") || values.text.includes("?")) {  
            errors.text = "! or ? cannot be username" 
          }
          return errors;
        }}
        onSubmit={(data, { setSubmitting }) => {
              setSubmitting(true) 
          console.log(data)
          sendMessage();
              setSubmitting(false) 
            }}>

            { 
                ({ isSubmitting, values }) => (
                <Form> 
                      <div>
                      <BasicTextField name="text" />  
                    </div>
                      <Button style={{border: "2px solid black"}} type="submit" color="default" disabled={isSubmitting} disableElevation disableRipple fullWidth>Submit</Button>
                </Form> 
                )  
              }

            </Formik>

      </TransitionLayout> 
    </>
  ); 
};


export default ChatPage;
