import React from 'react';
import styled from "styled-components";
import { Swiper, SwiperSlide } from "swiper/react";

const BasicSlideBannerWrapper = styled.div`
  width:100%;
  height:100%;
  display:flex;
  flex-flow: row wrap;

`;


const SwiperItem = styled(Swiper)`
  img{width:100%;}

  // 커스텀 애롱 사용시 주석 해제
  // .swiper-button-prev:after, .swiper-button-next:after{
  //   display:none;
  // }

  .swiper-button-prev,.swiper-button-next{
    color: #fff;
  }

  // 커스텀 애롱 사용시 주석 해제
  // .swiper-button-prev{
  //   background-image: url('../../asset/test.jpg');
  // }

  // .swiper-button-next{
  //   background-image: url('../../asset/test.jpg');
  // }

`;


interface BannerItem{
  id: number;
  img_url: string;
  target_url: string;
}

interface Props { 
  datas: BannerItem[]; 
}


const BasicSlideBanner = ({ datas, m, pc }:Props):JSX.Element=> { 

  const [windowWidth, setWindowWith] = React.useState(0); 
  const handleRedirect = (href: string) => {
    window.location.href = href;
    return;
  }

  React.useEffect(() => {
    setWindowWith(window.innerWidth);
  }, []);

  return (
    <BasicSlideBannerWrapper>
       <SwiperItem
        spaceBetween={10}  
        slidesPerView={windowWidth < 992 ? m : pc}  
        speed={400}
        navigation
        autoplay  
        pagination={{ clickable: true }}
        onSlideChange={() => console.log("slide change")}
        onSwiper={(swiper) => console.log(swiper)}
        loop={true} 
      >


        {datas.map((item:BannerItem) => (
          <SwiperSlide key={item.img_url + item.target_url}>
            <img src={item.img_url} alt="banner" onClick={()=>{ handleRedirect(item.target_url) }}/> 
          </SwiperSlide>          
        ))} 

      </SwiperItem> 
      {windowWidth}
    </BasicSlideBannerWrapper>
  )

}

export default BasicSlideBanner;
