import { BACKEND_URL } from 'config';
import styled from 'styled-components';
import useSWR from 'swr';
import axios from 'util/axios';
import TransitionLayout from 'components/layout/TransitionLayout';
import dayjs from 'dayjs';
import { useRef, useState } from 'react';
import { useAuthState } from 'lib/providers/authProvider';
import { useRouter } from 'next/router';
import { FaHeart } from 'react-icons/fa';
import { AiOutlineClockCircle } from 'react-icons/ai';

const BoardViewPageWrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-flow: row wrap;

  .board-header {
    width: 100%;
    border-bottom: 1px solid lightgrey;
    padding: 15px 0;
    .created {
      color: grey;
    }
  }
  .board-body {
    width: 100%;
    padding: 30px 15px;
    .author {
      width: 100%;
      margin-top: 40px;
      background: #ededed;
      padding: 15px 20px;
      border-radius: 10px;
    }
  }
  .comments {
    width: 100%;
    .comments-header {
      width: 100%;
    }
    .comment-form {
      width: 100%;
      height: 120px;
      margin-top: 15px;
      padding: 15px;
      background: #f8f8ff;
      border-radius: 5px;
      display: flex;
      flex-flow: row wrap;
      textarea {
        width: calc(100% - 80px);
        padding: 10px;
      }
      .comment-submit {
        width: 80px;
        background: #00aedb;
        color: #fff;
        font-size: 14px;
      }
    }
    .comment-list {
      width: 100%;
      .comment {
        width: 100%;
        padding: 15px;
        border-bottom: 1px solid lightgrey;

        .comment-footer {
          width: 100%;
          padding: 5px 0;
          display: flex;
          flex-flow: row wrap;
          .createdAt{
            width:100%;
            color:#d3d4d5;
            font-size:12px;
            margin-bottom:5px;
          }
          .like {
            font-size: 14px;
            margin-right: 25px;
            color grey;
            cursor:pointer;
            svg{
              position:relative;
              top:-1px;
              margin-right:3px;
            }
          }
          .reply {
            cursor:pointer;
            font-size: 14px;
            color grey;
          }
        }
      }
    }
  }
`;

interface Props {
  board: any;
}

const BoardViewPage = (props: Props): JSX.Element => {
  const me = useAuthState();
  const router = useRouter();
  const { bo_id } = router.query;

  const fetcher = async (url: string) => {
    const resp = await axios.get(url);
    return resp.data;
  };

  const { data, mutate, error } = useSWR(
    `${BACKEND_URL}/board/${bo_id}`,
    fetcher,
    { initialData: props.board }
  );

  if (!data) return <div>Loading</div>;
  if (error) return <div>ERROR</div>;
  return (
    <TransitionLayout title="채팅" requiredLogin={true}>
      <BoardViewPageWrapper>
        <div className="board-header">
          <h2>{data.title}</h2>
          <div className="created">
            {dayjs(data.createdAt).format('YYYY.MM.DD HH:mm')}
          </div>
        </div>

        <div className="board-body">
          <div dangerouslySetInnerHTML={{ __html: data.content }} />
          <div className="author">작성자 : {data.author}</div>
        </div>

        <Comments
          datas={data.comments}
          user_id={me.userInfo.user_id}
          parentId={bo_id.toString()}
          data={data}
          mutate={mutate}
        />
      </BoardViewPageWrapper>
    </TransitionLayout>
  );
};

interface IComments {
  datas: any;
  user_id: string;
  parentId: string;
  data: any;
  mutate: any;
}

const Comments = ({ user_id, parentId, datas, data, mutate }: IComments) => {
  const [content, setContent] = useState('');
  const contentRef = useRef(null);

  const handleChangeContent = (e: any) => {
    setContent(e.target.value);
    return;
  };

  const handleSubmit = () => {
    if (content === '') {
      alert('내용을 입력해주세요.');
      contentRef.current.focus();
      return;
    }

    console.log('user_id', parentId);

    axios.post(`${BACKEND_URL}/board/comment`, {
      content,
      author: user_id,
      parentId,
      topic: 'default',
    });

    mutate(data, false);
  };

  return (
    <div className="comments">
      <div className="comments-header">댓글 {datas.length}개</div>
      <div className="comment-form">
        <textarea ref={contentRef} onChange={handleChangeContent}>
          {content}
        </textarea>
        <button className="comment-submit" onClick={handleSubmit}>
          댓글 등록
        </button>
      </div>
      <div className="comment-list">
        {datas.map((item: any) => (
          <div key={item.id} className="comment">
            {item.author}
            {item.content}
            <div className="comment-footer">
              <div className="createdAt">
                <AiOutlineClockCircle />
                {dayjs(item.createdAt).format('YYYY.MM.DD HH:mm')}
              </div>
              <span className="like">
                <FaHeart />
                좋아요
              </span>
              <span className="reply">답글달기</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// SSR
export const getServerSideProps = async (context: any) => {
  const { bo_id } = context.query;

  const fetcher = async (url: string) => {
    const resp = await axios.get(url);
    return resp.data;
  };

  const board = await fetcher(`${BACKEND_URL}/board/${bo_id}`);
  return {
    props: {
      board,
    },
  };
};

export default BoardViewPage;
