import React from 'react';
import dynamic from 'next/dynamic';
import 'react-quill/dist/quill.snow.css';
// import * as Emoji from 'quill-emoji';
// import { Quill } from 'react-quill';
// import Emoji from 'quill-emoji';
// const Emoji = dynamic(() => import('quill-emoji'), { ssr: false });
const ReactQuill = dynamic(() => import('react-quill'), {
  ssr: false,
});

// const Quill2 = dynamic(() => Quill, {
//   ssr: false,
// });
// const Quill = dynamic(
//   () => import('react-quill').then((mod: any) => mod.Quill),
//   {
//     ssr: false,
//   }
// );

// const Roader = () => {
//   return <></>;
// };

// const TQuill = dynamic(() => Quill, {
//   ssr: false,
// });
// Quill.register('modules/emoji', Emoji);

// const Quill = dynamic(
//   () =>
//     import('react-quill').then((mod: any) => {
//       mod.Quill;
//     }),
//   {
//     ssr: false,
//   }
// );

// const Quill = typeof window == 'object' ? require('quill') : () => false;
// console.log(Quill);
// // Quill.register('modules/emoji', Emoji);

const CustomHeart = () => <span>♥</span>;

function insertHeart() {
  console.log('THIS-QUILL', this.quill);
  const cursorPosition = this.quill.getSelection().index;
  this.quill.insertText(cursorPosition, '♥');
  this.quill.setSelection(cursorPosition + 1);
}

function insertEmoji(emoji) {
  console.log('THIS-QUILL', this.quill);
  const cursorPosition = this.quill.getSelection().index;
  this.quill.insertText(cursorPosition, `${emoji}`);
  this.quill.setSelection(cursorPosition + 1);
}

const CustomToolbar = () => (
  <div id="toolbarr">
    <select className="ql-font">
      <option value="arial" selected>
        Arial
      </option>
      <option value="comic-sans">Comic Sans</option>
      <option value="courier-new">Courier New</option>
      <option value="georgia">Georgia</option>
      <option value="helvetica">Helvetica</option>
      <option value="lucida">Lucida</option>
    </select>
    <select className="ql-size">
      <option value="extra-small">Size 1</option>
      <option value="small">Size 2</option>
      <option value="medium" selected>
        Size 3
      </option>
      <option value="large">Size 4</option>
    </select>

    <select className="ql-align" />
    <select className="ql-color" />
    <select className="ql-background" />
    <button className="ql-image" />
    <button className="ql-video" />

    <button className="ql-clean" />
    <button className="ql-insertHeart">
      <CustomHeart />
    </button>
  </div>
);

const Editor2 = ({ val, setVal }: any) => {
  // const [content, setContent] = React.useState('');
  const [openEmoji, setOpenEmoji] = React.useState(false);

  function toggleEmojiBox() {
    setOpenEmoji(true);
  }

  const handleChange = (html: any) => {
    setVal(html);
  };

  const modules = {
    toolbar: {
      container: '#toolbarr',
      handlers: {
        insertHeart: insertHeart,
        emoji: toggleEmojiBox,
      },
    },
  };

  const formats = [
    'header',
    'font',
    'size',
    'bold',
    'italic',
    'underline',
    'strike',
    'blockquote',
    'list',
    'bullet',
    'indent',
    'link',
    'image',
    'color',
  ];
  return (
    <div className="text-editor">
      <CustomToolbar />
      <ReactQuill
        value={val}
        onChange={handleChange}
        modules={modules}
        // formats={Editor.formats}
      />
    </div>
  );
};

export default Editor2;
