import styled from "styled-components";
import { Formik, Form, useField, FieldAttributes} from "formik"
import { TextField, Button, Checkbox, FormControlLabel,Radio } from "@material-ui/core";
import BasicTextField from "./material/BasicTextField"; 

const TestWrapper = styled.div`
  width:100%;
  height:100%;
  display:flex;
  flex-flow: row wrap;
`;

interface Props { 

}

type RadioProps = { label: string } & FieldAttributes<{}>;

const LabeledRadio: React.FC<RadioProps> = ({ label, ...rest }) => {
  const [field,] = useField(rest);
  return (
    <FormControlLabel control={<Radio />} label={label} {...field} />
  )
} 



const Test = ({  }:Props):JSX.Element=> {

  return (
    <TestWrapper>
      <Formik initialValues={{ text: "", isbool: false, checks: [], nation: '' }}
        validate={values => {
          const errors: Record<string, string> = {} 
          // text에  !나 ? 가 들어가면 에러를 뿜는 예제 
          if (values.text.includes("!") || values.text.includes("?")) {  
            errors.text = "! or ? cannot be username" 
          }
          return errors;
        }}
        onSubmit={(data, { setSubmitting }) => {
              setSubmitting(true) 

              console.log(data)

              setSubmitting(false) 
            }}>

            { 
                ({ isSubmitting }) => (
                <Form> 
                      <div>
                      <BasicTextField name="text" />  
                    </div>
                    <div>
                      <LabeledCheckbox name="checks" value="React" label="React" type="checkbox" />
                      <LabeledCheckbox name="checks" value="Redux" label="Redux" type="checkbox" />
                      <LabeledCheckbox name="checks" value="Vue" label="Vue" type="checkbox" />
                      <LabeledCheckbox name="checks" value="MobX" label="MobX" type="checkbox" /> 
                    </div>
                    <div>
                      <div>

                        <LabeledRadio name="nation" value="Korea" label="Korea" type="radio" />
                        <LabeledRadio name="nation" value="Japan" label="Japan" type="radio" />
                        <LabeledRadio name="nation" value="China" label="China" type="radio" />
                      </div>
                    </div>

                      <Button type="submit" disabled={isSubmitting}>Submit</Button>
                </Form>
                ) 
              }

            </Formik>
    </TestWrapper>
  )

}

export default Test;
