import {Field, ErrorMessage, FieldArray } from "formik";
import { Button } from "@material-ui/core";
import styled from 'styled-components';
import BasicTextField from "./BasicTextField"; 

const FieldTile = styled.div`
  width:100%;
  padding:15px 30px;
  border 1px solid #1a1a1a;
  margin-bottom:15px;
  .toolbox{
    width:100%;
    position: relative;
    height:40px;
    .remove{
      position:absolute;
      right:0;
    }
  }
`;

const BasicFieldArray = ({ datas }) => {
  return (

    <FieldArray name="friends">
    {({ remove, push }) => ( 
      <div>
        {datas.length > 0 &&
          datas.map((friend, index) => ( 
            <FieldTile>
              <div className="toolbox">
                <button
                  type="button"
                  className="remove"
                  onClick={() => remove(index)}
                >
                  X
                </button>
                <Button style={{border: "2px solid black"}} type="submit" color="default" disableElevation disableRipple>Submit</Button>
              </div>

              <BasicTextField name={`friends.${index}.name`} />
              <BasicTextField name={`friends.${index}.email`} />


              {JSON.stringify(friend)}
            </FieldTile>
          ))}
        <button
          type="button"
          className="secondary"
          onClick={() => push({ name: '', email: '' })}
        >
          Add Friend
        </button>
      </div>
    )}
  </FieldArray>

  )
}


export default BasicFieldArray;
