import React from 'react';
import styled from 'styled-components';
import { FiHeart,FiBookmark } from "react-icons/fi";
import { AiOutlineShareAlt } from 'react-icons/ai';


interface IBoardItem {
  title: string;
  tags: string;
  wr_datetime: string;
  like_count: number;
  share_count: number;
  scrab_count: number;
}

const BoardListWrapper = styled.div`
  width:100%;
  height:100%;
  display:flex;
  flex-flow: row wrap;
  background:#F6F6F6;
`;
const BoardListItemWrapper = styled.div`
  width:100%;
  paddin:10px;
  display:flex; 
  flex-flow: row wrap;
  border-top:1px solid #e3e3e3;
  border-bottom:1px solid #e3e3e3;
  margin-bottom:10px;
  background:#fff;

  .tile_aside{
    width:30px; height:100%;
    text-align: center;
    border-right:1px solid #e3e3e3;
    span{
      font-size:12.5px;
      display:block;
    }
  }
  .tile_main{
    width: calc(100% - 30px);
     .head_title{
       font-size: 16px;
       line-height:10px;
     }
  }
`;


const dummy = [
  {
    title: "쓰레기통 안에 로또 당첨 복권 소유 권한은 누구한테 있나요?",
    tags: "법률|생활",
    wr_datetime: "2021-01-09 12:55:00",
    like_count: 291,
    share_count: 38,
    scrab_count: 22
  },
  {
    title: "쓰레기통 안에 로또 당첨 복권 소유 권한은 누구한테 있나요?",
    tags: "법률|생활",
    wr_datetime: "2021-01-09 12:55:00",
    like_count: 291,
    share_count: 38,
    scrab_count: 22
  },{
    title: "쓰레기통 안에 로또 당첨 복권 소유 권한은 누구한테 있나요?",
    tags: "법률|생활",
    wr_datetime: "2021-01-09 12:55:00",
    like_count: 291,
    share_count: 38,
    scrab_count: 22
  },{
    title: "쓰레기통 안에 로또 당첨 복권 소유 권한은 누구한테 있나요?",
    tags: "법률|생활",
    wr_datetime: "2021-01-09 12:55:00",
    like_count: 291,
    share_count: 38,
    scrab_count: 22
  },{
    title: "쓰레기통 안에 로또 당첨 복권 소유 권한은 누구한테 있나요?",
    tags: "법률|생활",
    wr_datetime: "2021-01-09 12:55:00",
    like_count: 291,
    share_count: 38,
    scrab_count: 22
  },{
    title: "쓰레기통 안에 로또 당첨 복권 소유 권한은 누구한테 있나요?",
    tags: "법률|생활",
    wr_datetime: "2021-01-09 12:55:00",
    like_count: 291,
    share_count: 38,
    scrab_count: 22
  },
];

const BoardList = () => {
  return (
    <BoardListWrapper>
      {dummy.map((item:IBoardItem):JSX.Element => (
        <BoardListItem item={item}/>
      ))}
    </BoardListWrapper>
  )
}

const BoardListItem = ({ item }):JSX.Element => {
  return (
    <BoardListItemWrapper>
      <div className='tile_aside'>
        <FiHeart />
        <span>{item.like_count}</span>
      </div>
      <div className='tile_main'>
        <p className="head_title">{item.title}</p>
        <div className="tag_wrap">
          {item.tags.split("|").map((item: string) => (
            <span className='tag'>{item}</span> 
          ))}
        </div>
        <div className='date_wrap'>{item.wr_datetime}</div>
        <div className='count_wrap'>
          <span className='share_label'><AiOutlineShareAlt />{item.share_count}</span>
          <span className='scrab_label'><FiBookmark />{item.scrab_count}</span> 
        </div>
      </div>
    </BoardListItemWrapper>
  )
}

export default BoardList;
