// import Link from "next/link";
import React, { useEffect } from 'react';
import Head from 'next/head';
import Cookies from 'js-cookie';
import { useRouter } from 'next/router';
import jwtDecode from 'jwt-decode';
import { BACKEND_URL } from '../../config';
import { Layout, Menu, Breadcrumb } from 'antd';
import {
  DesktopOutlined,
  PieChartOutlined,
  FileOutlined,
  TeamOutlined,
  UserOutlined,
} from '@ant-design/icons';
import 'antd/dist/antd.css';

const AdminLayout = ({ children, title, requiredLogin }: any) => {
  // const me = useAuthState();
  // const dispatch = useAuthDispatch();
  const Router = useRouter();
  const accessToken = Cookies.get('accessToken');
  const { Header, Content, Footer, Sider } = Layout;
  const { SubMenu } = Menu;

  const [collapesd, setCollapesd] = React.useState(false);
  const onCollapse = (collapsed: any) => {
    console.log(collapsed);
    setCollapesd(collapsed);
  };

  useEffect(() => {
    if (requiredLogin) {
      console.log('로그인 필수 페이지');
      if (!accessToken) {
        // 로그인되어있지 않다면 로그인창으로
        Router.push('/intro');
      }
    }

    if (!accessToken) {
      // 로그인되어있지 않다면 로그인창으로
      // Router.push("/login");
    } else {
      // 로그인 되어있다면

      console.log(BACKEND_URL + '/auth/' + jwtDecode<any>(accessToken).user_id);
      // axios.get(
      //   BACKEND_URL + "/auth/" + jwtDecode<any>(accessToken).user_id
      // ).then((res:any) => {
      //   console.log(res.data);
      //   const userInfo = res.data;
      //   dispatch({ type: "UPDATE_USER", userInfo });
      // })
    }
  }, []);

  return (
    <>
      {/* <Header /> */}
      {/* <Footer /> */}
      <Layout style={{ minHeight: '100vh' }}>
        <Sider collapsible collapsed={collapesd} onCollapse={onCollapse}>
          <div className="logo" />
          <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
            <Menu.Item key="1" icon={<PieChartOutlined />}>
              Option 1
            </Menu.Item>
            <Menu.Item key="2" icon={<DesktopOutlined />}>
              Option 2
            </Menu.Item>
            <SubMenu key="sub1" icon={<UserOutlined />} title="User">
              <Menu.Item key="3">Tom</Menu.Item>
              <Menu.Item key="4">Bill</Menu.Item>
              <Menu.Item key="5">Alex</Menu.Item>
            </SubMenu>
            <SubMenu key="sub2" icon={<TeamOutlined />} title="Team">
              <Menu.Item key="6">Team 1</Menu.Item>
              <Menu.Item key="8">Team 2</Menu.Item>
            </SubMenu>
            <Menu.Item key="9" icon={<FileOutlined />}>
              Files
            </Menu.Item>
          </Menu>
        </Sider>
        <Layout className="site-layout">
          <Header className="site-layout-background" style={{ padding: 0 }} />
          <Content style={{ margin: '0 16px' }}>
            <Breadcrumb style={{ margin: '16px 0' }}>
              <Breadcrumb.Item>User</Breadcrumb.Item>
              <Breadcrumb.Item>Bill</Breadcrumb.Item>
            </Breadcrumb>
            <div
              className="site-layout-background"
              style={{ padding: 24, minHeight: 360 }}
            >
              Bill is a cat.
            </div>
          </Content>
          <Footer style={{ textAlign: 'center' }}>
            Ant Design ©2018 Created by Ant UED
          </Footer>
        </Layout>
      </Layout>
    </>
  );
};

export default AdminLayout;
