import styled from "styled-components";
import SwiperCore, {
  Navigation,
  Autoplay,
  Pagination,
  A11y,
  EffectFade,
} from "swiper";
// 스와이퍼 메뉴얼은 이쪽 -> https://swiperjs.com/react/
// 데모 -> https://swiperjs.com/demos/
import { Swiper, SwiperSlide } from "swiper/react";



const BasicSlideBannerWrapper = styled.div`
  width:100%;
  height:100%;
  display:flex;
  flex-flow: row wrap;

`;


interface BannerItem{
  img_url: string;
  target_url: string;
}

interface Props { 
  datas: BannerItem[]; 
}

const BasicSlideBanner = ({ datas }:Props):JSX.Element=> {

  return (
    <BasicSlideBannerWrapper>
       <Swiper
        spaceBetween={0}  
        slidesPerView={1}
        speed={400}
        navigation
        autoplay  
        pagination={{ clickable: true }}
        onSlideChange={() => console.log("slide change")}
        onSwiper={(swiper) => console.log(swiper)}
        loop={true} 
      >
          <SwiperSlide key={img_url + target_url}>
              <img src={img_url} alt="banner" onClick={()=>{ handleRedirect(target_url) }}/>
          </SwiperSlide>
      </Swiper>
    </BasicSlideBannerWrapper>
  )

}

export default BasicSlideBanner;
