import { BACKEND_URL } from 'config';
import styled from 'styled-components';
import useSWR from 'swr';
import axios from 'util/axios';
import TransitionLayout from 'components/layout/TransitionLayout';
// import queryString from 'query-string';
import dayjs from 'dayjs';
const BoardViewPageWrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-flow: row wrap;

  .board-header {
    width: 100%;
  }
`;

interface Props {}

const BoardViewPage = ({}: Props): JSX.Element => {
  // const parsed = queryString.parse(location.search);
  const fetcher = async (url: string) => {
    const resp = await axios.get(url);
    return resp.data;
  };

  const { data, mutate, error } = useSWR(`${BACKEND_URL}/board/17`, fetcher);
  if (!data) return <div>Loading</div>;
  if (error) return <div>ERROR</div>;
  return (
    <TransitionLayout title="채팅" requiredLogin={true}>
      <BoardViewPageWrapper>
        <div className="board-header">
          <h2>{data.title}</h2>
          <div>{dayjs(data.createdAt).format('YYYY.MM.DD HH:mm')}</div>
        </div>

        <div dangerouslySetInnerHTML={{ __html: data.content }} />
        <div>{data.author}</div>
      </BoardViewPageWrapper>
    </TransitionLayout>
  );
};

export default BoardViewPage;
